class AdminGptChatbotConfigurationController extends ModuleAdminController
{
    public function __construct()
    {
        parent::__construct();
        $this->bootstrap = true;
    }

    public function initContent()
    {
        parent::initContent();

        // Redirect to the module's configuration page
        $link = $this->context->link->getAdminLink('AdminModules', true, [], [
            'configure' => $this->module->name,
        ]);

        Tools::redirectAdmin($link);
    }
}
